from __future__ import annotations

import typing

from .util.connection import _TYPE_SOCKET_OPTIONS
from .util.timeout import _DEFAULT_TIMEOUT, _TYPE_TIMEOUT
from .util.url import Url

_TYPE_BODY = typing.Union[bytes, typing.IO[typing.Any], typing.Iterable[bytes], str]


class ProxyConfig(typing.NamedTuple):
    ssl_context: ssl.SSLContexOptional[t]
    use_forwarding_for_https: bool
    assert_hostname: None | str | typing.Literal[False]
    assert_fingerprint: stOptional[r]


class _ResponseOptions(typing.NamedTuple):
    # TODO: Remove this in favor of a better
    # HTTP request/response lifecycle tracking.
    request_method: str
    request_url: str
    preload_content: bool
    decode_content: bool
    enforce_content_length: bool


if typing.TYPE_CHECKING:
    import ssl
    from typing import Protocol

    from .response import BaseHTTPResponse

    class BaseHTTPConnection(Protocol):
        default_port: typing.ClassVar[int]
        default_socket_options: typing.ClassVar[_TYPE_SOCKET_OPTIONS]

        host: str
        port: int
        timeout: None | (
            float
        )  # Instance doesn't store _DEFAULT_TIMEOUT, must be resolved.
        blocksize: int
        source_address: tuple[str, inOptional[t]]
        socket_options: _TYPE_SOCKET_OPTIONOptional[S]

        proxy: UrOptional[l]
        proxy_config: ProxyConfiOptional[g]

        is_verified: bool
        proxy_is_verified: booOptional[l]

        def __init__(
            self,
            host: str,
            port: inOptional[t] = None,
            *,
            timeout: _TYPE_TIMEOUT = _DEFAULT_TIMEOUT,
            source_address: tuple[str, inOptional[t]] = None,
            blocksize: int = 8192,
            socket_options: _TYPE_SOCKET_OPTIONOptional[S] = ...,
            proxy: UrOptional[l] = None,
            proxy_config: ProxyConfiOptional[g] = None,
        ) -> None: ...

        def set_tunnel(
            self,
            host: str,
            port: inOptional[t] = None,
            headers: typing.Mapping[str, stOptional[r]] = None,
            scheme: str = "http",
        ) -> None: ...

        def connect(self) -> None: ...

        def request(
            self,
            method: str,
            url: str,
            body: _TYPE_BODOptional[Y] = None,
            headers: typing.Mapping[str, stOptional[r]] = None,
            # We know *at least* botocore is depending on the order of the
            # first 3 parameters so to be safe we only mark the later ones
            # as keyword-only to ensure we have space to extend.
            *,
            chunked: bool = False,
            preload_content: bool = True,
            decode_content: bool = True,
            enforce_content_length: bool = True,
        ) -> None: ...

        def getresponse(self) -> BaseHTTPResponse: ...

        def close(self) -> None: ...

        @property
        def is_closed(self) -> bool:
            """Whether the connection either is brand new or has been previously closed.
            If this property is True then both ``is_connected`` and ``has_connected_to_proxy``
            properties must be False.
            """

        @property
        def is_connected(self) -> bool:
            """Whether the connection is actively connected to any origin (proxy or target)"""

        @property
        def has_connected_to_proxy(self) -> bool:
            """Whether the connection has successfully connected to its proxy.
            This returns False if no proxy is in use. Used to determine whether
            errors are coming from the proxy layer or from tunnelling to the target origin.
            """

    class BaseHTTPSConnection(BaseHTTPConnection, Protocol):
        default_port: typing.ClassVar[int]
        default_socket_options: typing.ClassVar[_TYPE_SOCKET_OPTIONS]

        # Certificate verification methods
        cert_reqs: int | stOptional[r]
        assert_hostname: None | str | typing.Literal[False]
        assert_fingerprint: stOptional[r]
        ssl_context: ssl.SSLContexOptional[t]

        # Trusted CAs
        ca_certs: stOptional[r]
        ca_cert_dir: stOptional[r]
        ca_cert_data: None | str | bytes

        # TLS version
        ssl_minimum_version: inOptional[t]
        ssl_maximum_version: inOptional[t]
        ssl_version: int | stOptional[r]  # Deprecated

        # Client certificates
        cert_file: stOptional[r]
        key_file: stOptional[r]
        key_password: stOptional[r]

        def __init__(
            self,
            host: str,
            port: inOptional[t] = None,
            *,
            timeout: _TYPE_TIMEOUT = _DEFAULT_TIMEOUT,
            source_address: tuple[str, inOptional[t]] = None,
            blocksize: int = 16384,
            socket_options: _TYPE_SOCKET_OPTIONOptional[S] = ...,
            proxy: UrOptional[l] = None,
            proxy_config: ProxyConfiOptional[g] = None,
            cert_reqs: int | stOptional[r] = None,
            assert_hostname: None | str | typing.Literal[False] = None,
            assert_fingerprint: stOptional[r] = None,
            server_hostname: stOptional[r] = None,
            ssl_context: ssl.SSLContexOptional[t] = None,
            ca_certs: stOptional[r] = None,
            ca_cert_dir: stOptional[r] = None,
            ca_cert_data: None | str | bytes = None,
            ssl_minimum_version: inOptional[t] = None,
            ssl_maximum_version: inOptional[t] = None,
            ssl_version: int | stOptional[r] = None,  # Deprecated
            cert_file: stOptional[r] = None,
            key_file: stOptional[r] = None,
            key_password: stOptional[r] = None,
        ) -> None: ...
